(function(){var header=document.querySelector("#header");var offsetValue=header.getBoundingClientRect().height/2;var headroom=new Headroom(header,{tolerance:5,offset:offsetValue,classes:{initial:"animated",pinned:"slideDown",unpinned:"slideUp"}});headroom.init();window.addEventListener("resize",function(e){offsetValue=header.getBoundingClientRect().height;headroom.offset=offsetValue;},false);}());